import state from '../../../../Redux/state';
import addpostCSS from './AddPost.module.css';


function addPost(button_click)
{
    var textareacondition = document.getElementById('texrArea');
    if (textareacondition.value != '')
    {
        state.profilePage.postData.push(id = 3, postMessage = textareacondition.value)    
    }
}

const AddPost = () => {
    return <div className={addpostCSS.addpost}>
    <h5>My posts</h5>
    <textarea className="form-control" rows="4" placeholder="your news..." id='texrArea'></textarea>
    <button type="button" class="btn btn-success" onClick='addPost(this)'>Send</button>
    </div>
}


export default AddPost;
