import state from '../../../../Redux/state';
import addpostCSS from './AddPost.module.css';

let newPostElement = React.createRef();

function addPost(button_click)
{
    debugger;
    var textareacondition = document.getElementById('textArea').value;
    if (textareacondition != '')
    {
        state.profilePage.postData.push({id: 3, postMessage: textareacondition})    
    }
}



const AddPost = () => {
    return <div className={addpostCSS.addpost}>
    <h5>My posts</h5>
    <textarea className="form-control" rows="4" placeholder="your news..." id='textArea'></textarea>
    <button type="button" class="btn btn-success" onClick={ () => {addPost(this)}}>Send</button>
    </div>
}


export default AddPost;
